'use strict';

// MODULES //

var tape = require( 'tape' );
var exists = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof exists, 'function', 'export is a function' );
	t.end();
});

tape( 'export includes a function to test for existence synchronously', function test( t ) {
	t.equal( typeof exists.sync, 'function', 'export is a function' );
	t.end();
});
