'use strict';

// MODULES //

var tape = require( 'tape' );
var readDir = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof readDir, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a function to read a directory synchronously', function test( t ) {
	t.equal( typeof readDir.sync, 'function', 'attached method for synchronously reading a directory' );
	t.end();
});
