'use strict';

// MODULES //

var tape = require( 'tape' );
var fs = require( 'fs' );
var readDir = require( './../lib/sync.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof readDir, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function reads a directory', function test( t ) {
	var expected;
	var actual;

	expected = fs.readdirSync( __dirname );
	actual = readDir( __dirname );

	t.deepEqual( expected, actual, 'returns directory contents' );

	t.end();
});

tape( 'if the function encounters an error, the function returns the error', function test( t ) {
	var out = readDir( 'beepboopbapbop' );

	t.equal( out instanceof Error, true, 'returns an error' );

	t.end();
});
