'use strict';

// MODULES //

var tape = require( 'tape' );
var readFile = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof readFile, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a function to read an entire file synchronously', function test( t ) {
	t.equal( typeof readFile.sync, 'function', 'attached method for synchronously reading file contents' );
	t.end();
});
