'use strict';

/**
* Function sequence.
*
* @module @stdlib/utils/function-sequence
*
* @example
* var funseq = require( '@stdlib/utils/function-sequence' );
*
* function a( x ) {
*     return 2 * x;
* }
*
* function b( x ) {
*     return x + 3;
* }
*
* function c( x ) {
*     return x / 5;
* }
*
* var f = funseq( a, b, c );
*
* var z = f( 6 );
* // returns 3
*/

// MODULES //

var funseq = require( './funseq.js' );


// EXPORTS //

module.exports = funseq;
