'use strict';

/**
* Group values according to an indicator function.
*
* @module @stdlib/utils/group-by-async
*
* @example
* var readFile = require( '@stdlib/fs/read-file' );
* var groupByAsync = require( '@stdlib/utils/group-by-async' );
*
* var files = [
*     './beep.js',
*     './boop.js'
* ];
*
* function done( error, result ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( result );
* }
*
* function indicator( file, next ) {
*     var opts = {
*         'encoding': 'utf8'
*     };
*     readFile( file, opts, onFile );
*
*     function onFile( error ) {
*         if ( error ) {
*             return next( null, 'nonreadable' );
*         }
*         next( null, 'readable' );
*     }
* }
*
* groupByAsync( files, indicator, done );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var groupByAsync = require( './group_by.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( groupByAsync, 'factory', factory );


// EXPORTS //

module.exports = groupByAsync;
