'use strict';

// MODULES //

var isCollection = require( '@stdlib/assert/is-collection' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );


// MAIN //

/**
* Groups values as arrays associated with distinct keys.
*
* @param {Collection} collection - collection to group
* @param {Collection} groups - collection defining which group an element in the input collection belongs to
* @throws {TypeError} first argument must be a collection
* @throws {TypeError} second argument must be a collection
* @throws {RangeError} first and second arguments must have the same length
* @returns {Object} group results
*
* @example
* var arr = [ 'beep', 'boop', 'foo', 'bar' ];
* var groups = [ 'b', 'b', 'f', 'b' ];
*
* var out = group( arr, groups );
* // returns { 'b': [ 'beep', 'boop', 'bar' ], 'f': [ 'foo' ] }
*/
function group( collection, groups ) {
	var out;
	var len;
	var g;
	var i;
	if ( !isCollection( collection ) ) {
		throw new TypeError( 'invalid input argument. First argument must be a collection. Value: `'+collection+'`.' );
	}
	if ( !isCollection( groups ) ) {
		throw new TypeError( 'invalid input argument. Second argument must be a collection. Value: `'+groups+'`.' );
	}
	len = collection.length;
	if ( groups.length !== len ) {
		throw new RangeError( 'invalid input arguments. First and second arguments must be the same length.' );
	}
	out = {};
	for ( i = 0; i < len; i++ ) {
		g = groups[ i ].toString();

		// Checking for an own property kills performance, but is necessary to guard against the edge case where a user provides "groups" which match a method or property on the `Object` prototype...
		if ( hasOwnProp( out, g ) ) {
			out[ g ].push( collection[ i ] );
		} else {
			out[ g ] = [ collection[ i ] ];
		}
	}
	return out;
} // end FUNCTION group()


// EXPORTS //

module.exports = group;
