/* eslint-disable no-prototype-builtins */
'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var isBoolean = require( '@stdlib/utils/is-boolean' ).isPrimitive;
var pkg = require( './../package.json' ).name;
var hasOwnProp = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var bool;
	var obj;
	var key;
	var i;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		key = String.fromCharCode( 97+(i%26) );
		obj = {};
		obj[ key ] = i;
		bool = hasOwnProp( bool, key );
		if ( !isBoolean( bool ) ) {
			b.fail( 'should return a boolean' );
		}
	}
	b.toc();
	if ( !isBoolean( bool ) ) {
		b.fail( 'should return a boolean' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::native', function benchmark( b ) {
	var bool;
	var obj;
	var key;
	var i;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		key = String.fromCharCode( 97+(i%26) );
		obj = {};
		obj[ key ] = i;
		bool = obj.hasOwnProperty( key );
		if ( !isBoolean( bool ) ) {
			b.fail( 'should return a boolean' );
		}
	}
	b.toc();
	if ( !isBoolean( bool ) ) {
		b.fail( 'should return a boolean' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
