/* eslint-disable object-curly-newline, object-curly-spacing */
'use strict';

var hasProp = require( './../lib' );

var bool = hasProp( { 'a': 'b' }, 'a' );
console.log( bool );
// => true

bool = hasProp( {}, 'hasOwnProperty' );
console.log( bool );
// => true

bool = hasProp( { 'a': 'b' }, 'c' );
console.log( bool );
// => false

bool = hasProp( { 'a': 'b' }, null );
console.log( bool );
// => false

bool = hasProp( null, 'a' );
console.log( bool );
// => false

bool = hasProp( void 0, 'a' );
console.log( bool );
// => false

bool = hasProp( { 'null': false }, null );
console.log( bool );
// => true

bool = hasProp( { '[object Object]': false }, {} );
console.log( bool );
// => true

