'use strict';

var randu = require( '@stdlib/random/base/randu' );
var ifelseAsync = require( './../lib' );

var i;

function next() {
	ifelseAsync( predicate, 'BOOP', 'beep', done );
}

function predicate( clbk ) {
	setTimeout( onTimeout, 0 );
	function onTimeout() {
		clbk( null, randu() > 0.9 );
	}
}

function done( error, result ) {
	if ( error ) {
		throw error;
	}
	i += 1;
	console.log( result );
	if ( i < 100 ) {
		next();
	}
}

i = 0;
next();
