'use strict';

// MODULES //

var tape = require( 'tape' );
var getPrototype = require( '@stdlib/utils/get-prototype-of' );
var createObject = require( './../lib/polyfill.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof createObject, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an `object` whose prototype equals the provided prototype', function test( t ) {
	var proto;
	var obj;

	proto = {
		'beep': 'boop'
	};
	obj = createObject( proto );

	t.strictEqual( getPrototype( obj ), proto, 'returns an object with provided prototype' );
	t.end();
});
