'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var pkg = require( './../package.json' ).name;
var inmapRight = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var arr;
	var len;
	var o;
	var i;

	function clbk( v, i ) {
		return v * i;
	}

	arr = new Array( 100 );
	len = arr.length;
	for ( i = 0; i < len; i++ ) {
		arr[ i ] = EPS;
	}
	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr[ 0 ] += 10.0;
		o = inmapRight( arr, clbk );
		if ( o !== arr ) {
			b.fail( 'should return input array' );
		}
	}
	b.toc();
	if ( o !== arr ) {
		b.fail( 'should return input array' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::built-in', function benchmark( b ) {
	var arr;
	var len;
	var i;

	function clbk( v, i ) {
		return v * i;
	}

	arr = new Array( 100 );
	len = arr.length;
	for ( i = 0; i < len; i++ ) {
		arr[ i ] = EPS;
	}
	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr[ 0 ] += 10.0;
		arr = arr.map( clbk );
		if ( arr.length === 0 ) {
			b.fail( 'should not be empty' );
		}
	}
	b.toc();
	if ( arr.length === 0 ) {
		b.fail( 'should not be empty' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::loop', function benchmark( b ) {
	var arr;
	var len;
	var i;
	var j;

	function clbk( v, i ) {
		return v * i;
	}

	arr = new Array( 100 );
	len = arr.length;
	for ( i = 0; i < len; i++ ) {
		arr[ i ] = EPS;
	}
	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr[ 0 ] += 10.0;
		for ( j = arr.length-1; j >= 0; j-- ) {
			arr[ j ] = clbk( arr[ j ], j );
		}
		if ( arr.length === 0 ) {
			b.fail( 'should not be empty' );
		}
	}
	b.toc();
	if ( arr.length === 0 ) {
		b.fail( 'should not be empty' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::this_context', function benchmark( b ) {
	var arr;
	var len;
	var o;
	var i;

	function clbk( v, i ) {
		return v * i;
	}

	arr = new Array( 100 );
	len = arr.length;
	for ( i = 0; i < len; i++ ) {
		arr[ i ] = EPS;
	}
	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr[ 0 ] += 10.0;
		o = inmapRight( arr, clbk, {} );
		if ( o !== arr ) {
			b.fail( 'should return input array' );
		}
	}
	b.toc();
	if ( o !== arr ) {
		b.fail( 'should return input array' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
