'use strict';

var isEven = require( '@stdlib/assert/is-even' ).isPrimitive;
var inmapRight = require( './../lib' );

var bool;
var arr;
var out;
var i;

function scale( value, index, collection ) {
	i += 1;
	if ( isEven( i ) ) {
		collection.pop();
	} else {
		collection.unshift( i+1 );
	}
	return value * index;
}

arr = new Array( 100 );
for ( i = 0; i < arr.length; i++ ) {
	arr[ i ] = i;
}

i = 0;
out = inmapRight( arr, scale );

bool = ( out === arr );
console.log( bool );

console.log( out );
