'use strict';

var isEven = require( '@stdlib/assert/is-even' ).isPrimitive;
var inmap = require( './../lib' );

var bool;
var arr;
var out;
var i;

function scale( value, index, collection ) {
	if ( isEven( index ) ) {
		collection.shift();
	} else {
		collection.push( index+1 );
	}
	return value * index;
}

arr = new Array( 100 );
for ( i = 0; i < arr.length; i++ ) {
	arr[ i ] = i;
}
out = inmap( arr, scale );

bool = ( out === arr );
console.log( bool );

console.log( out );
