'use strict';

var isAbsolutePath = require( './../lib' );

var bool = isAbsolutePath.posix( '/foo/bar/baz' );
console.log( bool );
// => true

bool = isAbsolutePath.posix( '/foo/..' );
console.log( bool );
// => true

bool = isAbsolutePath.posix( 'foo/' );
console.log( bool );
// => false

bool = isAbsolutePath.posix( 'foo' );
console.log( bool );
// => false

bool = isAbsolutePath.posix( '.' );
console.log( bool );
// => false

bool = isAbsolutePath.posix( '' );
console.log( bool );
// => false

bool = isAbsolutePath.win32( 'C:\\foo\\bar\\baz' );
console.log( bool );
// => true

bool = isAbsolutePath.win32( '//server' );
console.log( bool );
// => true

bool = isAbsolutePath.win32( '\\\\server' );
console.log( bool );
// => true

bool = isAbsolutePath.win32( 'C:/foo/bar/baz' );
console.log( bool );
// => true

bool = isAbsolutePath.win32( '/foo/..' );
console.log( bool );
// => true

bool = isAbsolutePath.win32( 'foo\\bar\\baz' );
console.log( bool );
// => false

bool = isAbsolutePath.win32( 'foo/bar/baz' );
console.log( bool );
// => false

bool = isAbsolutePath.win32( 'foo/..' );
console.log( bool );
// => false

bool = isAbsolutePath.win32( '.' );
console.log( bool );
// => false

bool = isAbsolutePath.win32( '' );
console.log( bool );
// => false
