'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// IS ABSOLUTE PATH //

/**
* Tests if a string is a POSIX absolute path.
*
* @param {string} str - string to test
* @throws {TypeError} must provide a string
* @returns {boolean} boolean indicating if a POSIX absolute path
*
* @example
* var bool = isAbsolutePath( '/foo/bar/baz' );
* // returns true
*
* @example
* var bool = isAbsolutePath( 'foo/bar/baz' );
* // returns false
*/
function isAbsolutePath( str ) {
	if ( !isString( str ) ) {
		throw new TypeError( 'invalid input argument. Must provide a string. Value: `'+str+'`.' );
	}
	// Must be longer than `0` characters and start with a forward slash '/':
	return (
		str.length > 0 &&
		str.charCodeAt( 0 ) === 47
	);
} // end FUNCTION isAbsolutePath()


// EXPORTS //

module.exports = isAbsolutePath;
