'use strict';

var isArguments = require( './../lib' );

function foo() {
	return arguments;
}

var bool = isArguments( foo() );
console.log( bool );
// => true

bool = isArguments( [] );
console.log( bool );
// => false

bool = isArguments( {} );
console.log( bool );
// => false

bool = isArguments( null );
console.log( bool );
// => false

bool = isArguments( 'Arguments' );
console.log( bool );
// => false

function Arguments() {
	return this;
}
bool = isArguments( new Arguments() );
console.log( bool );
// => false
