'use strict';

/**
* Test if a value is an array of arrays.
*
* @module @stdlib/utils/is-array-array
*
* @example
* var isArrayArray = require( '@stdlib/utils/is-array-array' );
*
* var bool = isArrayArray( [ [], [] ] );
* // returns true
*
* bool = isArrayArray( [ {}, {} ] );
* // returns false
*
* bool = isArrayArray( [] );
* // returns false
*/

// MODULES //

var arrayfun = require( '@stdlib/utils/tools/array-function' );
var isArray = require( '@stdlib/utils/is-array' );


// MAIN //

var isArrayArray = arrayfun.create( isArray );


// EXPORTS //

module.exports = isArrayArray;
