'use strict';

// MODULES //

var tape = require( 'tape' );
var isArrayLike = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isArrayLike, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided an array-like object', function test( t ) {
	var values = [
			[],
			{'length': 10},
			'beep',
			arguments
		];

		for ( var i = 0; i < values.length; i++ ) {
			t.ok( isArrayLike( values[i] ), values[i] );
		}
	t.end();
});

tape( 'function returns `false` if not provided an array-like object', function test( t ) {
	var values = [
		5,
		null,
		undefined,
		NaN,
		true,
		false,
		{},
		function boop( a, b, c ) {}
	];

	for ( var i = 0; i < values.length; i++ ) {
		t.ok( !isArrayLike( values[i] ) );
	}
	t.end();
});
