'use strict';

/**
* Tests if a value is a binary string.
*
* @module @stdlib/utils/is-binary-string
*
* @example
* var isBinaryString = require( '@stdlib/utils/is-binary-string' );
*
* var bool = isBinaryString( '1000101' );
* // returns true
*
* bool = isBinaryString( 'beep' );
* // returns false
*
* bool = isBinaryString( '' );
* // returns false
*/

// MODULES //

var isBinaryString = require( './is_binary_string.js' );


// EXPORTS //

module.exports = isBinaryString;
