'use strict';

var isBooleanArray = require( './../lib' );

var bool = isBooleanArray( [ true, false ] );
console.log( bool );
// => true

bool = isBooleanArray( [ true, new Boolean( false ) ] );
console.log( bool );
// => true

bool = isBooleanArray( [ true, 'false' ] );
console.log( bool );
// => false

bool = isBooleanArray( [] );
console.log( bool );
// => false

bool = isBooleanArray( null );
console.log( bool );
// => false
