'use strict';

// MODULES //

var tape = require( 'tape' );
var isBoolean = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isBoolean, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function provides a method to test for a generic boolean array', function test( t ) {
	var arr;

	arr = [ true, new Boolean( true ), false, new Boolean( false ) ];
	t.equal( isBoolean.isArray( arr ), true, 'returns true' );

	arr = [ true, 'true', null ];
	t.equal( isBoolean.isArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for a primitive boolean array', function test( t ) {
	var arr;

	arr = [ true, false, true, false ];
	t.equal( isBoolean.isPrimitiveArray( arr ), true, 'returns true' );

	arr = [ new Boolean( true ), false, false ];
	t.equal( isBoolean.isPrimitiveArray( arr ), false, 'returns false' );

	t.end();
});
