'use strict';

// MODULES //

var tape = require( 'tape' );
var isBoolean = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isBoolean, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a primitive boolean', function test( t ) {
	t.equal( isBoolean( true ), true, 'returns true' );
	t.end();
});

tape( 'function returns `true` if provided a Boolean object', function test( t ) {
	t.equal( isBoolean( new Boolean( 0 ) ), true, 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided a boolean', function test( t ) {
	var values;
	var i;

	values = [
		5,
		[],
		'5',
		function(){},
		null,
		{},
		NaN,
		undefined
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isBoolean( values[i] ), false, 'returns false when provided ' + values[i] );
	}
	t.end();
});
