'use strict';

// MODULES //

var tape = require( 'tape' );
var isBoolean = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isBoolean, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive boolean', function test( t ) {
	t.equal( typeof isBoolean.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a boolean object', function test( t ) {
	t.equal( typeof isBoolean.isObject, 'function', 'export is a function' );
	t.end();
});
