'use strict';

/**
* Boolean indicating if the current process is running on Darwin.
*
* @module @stdlib/utils/is-darwin
* @type {boolean}
*
* @example
* var isDarwin = require( '@stdlib/utils/is-darwin' );
*
* if ( isDarwin ) {
*     console.log( 'Running on Darwin...' );
* } else {
*     console.log( 'Running on %s...', process.platform );
* }
*/

// MODULES //

var platform = require( '@stdlib/utils/platform' );


// IS DARWIN //

/**
* Boolean indicating if the current process is running on Darwin.
*
* @constant
* @type {boolean}
*/
var isDarwin = ( platform === 'darwin' );


// EXPORTS //

module.exports = isDarwin;
