'use strict';

/**
* Test whether a string contains only numeric digits.
*
* @module @stdlib/utils/is-digit
*
* @example
* var isDigit = require( '@stdlib/utils/is-digit' );
*
* var out = isDigit( '0123456789' );
* // returns true
*
* out = isDigit( '0xffffff' );
* // returns false
*
* out = isDigit( '' );
* // returns false
*/

// MODULES //

var isDigit = require( './is_digit.js' );


// EXPORTS //

module.exports = isDigit;
