'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// VARIABLES //

// Character codes:
var ZERO = 48;
var NINE = 57;


// MAIN //

/**
* Tests whether a string contains only numeric digits.
*
* @param {*} x - value to test
* @returns {boolean} boolean indicating if a string contains only numeric digits
*
* @example
* var out = isDigit( '0123456789' );
* // returns true
*
* @example
* var out = isDigit( '0xffffff' );
* // returns false
*
* @example
* var out = isDigit( '' );
* // returns false
*
* @example
* var out = isDigit( 123 );
* // returns false
*/
function isDigit( x ) {
	var len;
	var ch;
	var i;
	if ( !isString( x ) ) {
		return false;
	}
	len = x.length;
	if ( !len ) {
		return false;
	}
	for ( i = 0; i < len; i++ ) {
		ch = x.charCodeAt( i );
		if ( ch < ZERO || ch > NINE ) {
			return false;
		}
	}
	return true;
} // end FUNCTION isDigit()


// EXPORTS //

module.exports = isDigit;
