'use strict';

// MODULES //

var tape = require( 'tape' );
var Global = require( 'system.global' )();
var proxyquire = require( 'proxyquire' );
var isElectronMain = require( './../lib/is_electron_main.js' );


// FIXTURES //

function process() {
	/* jshint validthis:true */ // TODO: eslint
	this.type = 'browser';
	this.versions = {
		'electron': '1.0.0',
		'chrome': '42.1.34'
	};
	return this;
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isElectronMain, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if runtime is the main Electron process', function test( t ) {
	var isElectronMain;
	var proc;
	var bool;

	proc = Global.process;
	Global.process = new process();

	isElectronMain = proxyquire( './../lib/is_electron_main.js', {
		'@stdlib/utils/is-electron': true
	});

	bool = isElectronMain();
	Global.process = proc;

	t.equal( bool, true, 'returns true' );

	t.end();
});

tape( 'the function returns `false` if runtime is not the main Electron process (not Electron)', function test( t ) {
	var isElectronMain;
	var proc;
	var bool;

	proc = Global.process;
	Global.process = new process();

	isElectronMain = proxyquire( './../lib/is_electron_main.js', {
		'@stdlib/utils/is-electron': false
	});

	bool = isElectronMain();
	Global.process = proc;

	t.equal( bool, false, 'returns false' );

	t.end();
});

tape( 'the function returns `false` if runtime is not the main Electron process (process type does not equal `browser`)', function test( t ) {
	var isElectronMain;
	var proc;
	var bool;

	proc = Global.process;
	Global.process = new process();
	Global.process.type = 'renderer';

	isElectronMain = proxyquire( './../lib/is_electron_main.js', {
		'@stdlib/utils/is-electron': true
	});

	bool = isElectronMain();
	Global.process = proc;

	t.equal( bool, false, 'returns false' );

	t.end();
});
