'use strict';

// MODULES //

var isElectron = require( '@stdlib/utils/is-electron' );


// IS ELECTRON RENDERER //

/**
* Returns a boolean indicating if the runtime is the Electron renderer process.
*
* @returns {boolean} boolean indicating if runtime is the Electron renderer process
*
* @example
* var bool = isElectronRenderer;
* // returns <boolean>
*/
function isElectronRenderer() {
	return (
		isElectron &&
		process.type === 'renderer'
	);
} // end FUNCTION isElectronRenderer()


// EXPORTS //

module.exports = isElectronRenderer;
