'use strict';

// MODULES //

var tape = require( 'tape' );
var isBrowser = require( './../lib/browser.js' );


// TESTS //

tape( 'main export is a boolean', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isBrowser, 'boolean', 'main export is a boolean' );
	t.end();
});

tape( 'the exported value is always `false`', function test( t ) {
	t.equal( isBrowser, false, 'returns false' );
	t.end();
});
