'use strict';

// MODULES //

var tape = require( 'tape' );
var isEmptyArray = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isEmptyArray, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided an empty array', function test( t ) {
	t.equal( isEmptyArray( [] ), true, 'returns true' );
	t.equal( isEmptyArray( new Array() ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided an empty array', function test( t ) {
	var values;
	var i;

	values = [
		5,
		{},
		true,
		'5',
		null,
		NaN,
		[1,2,3],
		new Array( 10 ),
		function(){},
		undefined
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isEmptyArray( values[i] ), false, 'returns false for when provided ' + values[i] );
	}
	t.end();
});
