'use strict';

var isEven = require( './../lib' );

console.log( isEven( 4.0 ) );
// returns true

console.log( isEven( 0.0 ) );
// returns true

console.log( isEven( new Number( 4 ) ) );
// returns true

console.log( isEven( 5.0 ) );
// returns false

console.log( isEven( NaN ) );
// returns false

console.log( isEven( '5' ) );
// returns false

console.log( isEven( null ) );
// returns false
