'use strict';

// MODULES //

var isInteger = require( '@stdlib/utils/is-integer' ).isObject;


// IS EVEN //

/**
* Tests if a value is a number object that is an even number.
*
* @param {*} value - value to test
* @returns {boolean} boolean indicating if a value is a number object that is an even number
*
* @example
* var bool = isEven( 4.0 );
* // returns false
* @example
* var bool = isEven( new Number( 4.0 ) );
* // returns true
*/
function isEven( value ) {
	return (
		isInteger( value ) &&
		value % 2 === 0
	);
} // end FUNCTION isEven()


// EXPORTS //

module.exports = isEven;
