'use strict';

// MODULES //

var tape = require( 'tape' );
var isEven = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isEven, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function provides a method to test for a generic number array containing only even numbers', function test( t ) {
	var arr;

	arr = [ 4.0, new Number( 4 ), -4.0 ];
	t.equal( isEven.isEvenArray( arr ), true, 'returns true' );

	arr = [ 4.0, '3', null ];
	t.equal( isEven.isEvenArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for a primitive number array containing only even numbers', function test( t ) {
	var arr;

	arr = [ 4.0, -0.0 ];
	t.equal( isEven.isPrimitiveEvenArray( arr ), true, 'returns true' );

	arr = [ new Number( 5 ), 1.0, 1.0 ];
	t.equal( isEven.isPrimitiveEvenArray( arr ), false, 'returns false' );

	t.end();
});
