'use strict';

// MODULES //

var tape = require( 'tape' );
var isEven = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isEven, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive even number', function test( t ) {
	t.equal( typeof isEven.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for an even number object', function test( t ) {
	t.equal( typeof isEven.isObject, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a generic number array containing only even numbers', function test( t ) {
	t.equal( typeof isEven.isEvenArray, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number array containing only even numbers', function test( t ) {
	t.equal( typeof isEven.isPrimitiveEvenArray, 'function', 'export is a function' );
	t.end();
});
