'use strict';

/**
* Test if a value is an array-like object containing only functions.
*
* @module @stdlib/utils/is-function-array
*
* @example
* var isFunctionArray = require( '@stdlib/utils/is-function-array' );
*
* function beep(){}
* function boop(){}
*
* var bool = isFunctionArray( [ beep, boop ] );
* // returns true
*
* bool = isFunctionArray( [ {}, beep ] );
* // returns false
*
* bool = isFunctionArray( [] );
* // returns false
*/

// MODULES //

var arrayfun = require( '@stdlib/utils/tools/array-like-function' );
var isFunction = require( '@stdlib/utils/is-function' );


// MAIN //

var isFunctionArray = arrayfun.create( isFunction );


// EXPORTS //

module.exports = isFunctionArray;
