'use strict';

var isFunction = require( './../lib' );

console.log( isFunction( function foo(){} ) );
// => true

console.log( isFunction( 'beep' ) );
// => false

console.log( isFunction( 5 ) );
// => false

console.log( isFunction( true ) );
// => false

console.log( isFunction( null ) );
// => false

console.log( isFunction( [] ) );
// => false

console.log( isFunction( {} ) );
// => false
