'use strict';

// MODULES //

var tape = require( 'tape' );
var isFunction = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isFunction, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function has a method to test if a value is a function array', function test( t ) {
	var bool;
	var arr;

	function beep(){}
	function boop(){}

	arr = [ beep, boop ];
	bool = isFunction.isFunctionArray( arr );
	t.equal( bool, true, 'returns true' );

	arr = [ beep, null ];
	bool = isFunction.isFunctionArray( arr );
	t.equal( bool, false, 'returns false' );

	arr = [];
	bool = isFunction.isFunctionArray( arr );
	t.equal( bool, false, 'returns false when provided a function array' );

	t.end();
});
