'use strict';

// MODULES //

var tape = require( 'tape' );
var isFunction = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isFunction, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to main export is a method to test if a value is a function array', function test( t ) {
	t.equal( typeof isFunction.isFunctionArray, 'function', 'has method' );
	t.end();
});

tape( 'the function returns `true` if provided a function', function test( t ) {
	t.equal( isFunction( function beep(){} ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a function', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		undefined,
		true,
		null,
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isFunction( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
