'use strict';

// MODULES //

var tape = require( 'tape' );
var isFunction = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isFunction, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a function', function test( t ) {
	t.ok( isFunction( function(){} ), 'returns true' );
	t.end();
});

tape( 'function returns `false` if not provided a function', function test( t ) {
	var values = [
		5,
		[],
		true,
		'5',
		null,
		{},
		NaN,
		undefined
	];

	for ( var i = 0; i < values.length; i++ ) {
		t.ok( !isFunction( values[i] ),  false, 'returns false for value: ' + values[i] );
	}
	t.end();
});
