/* eslint-disable no-new-wrappers */
'use strict';

var isIntegerArray = require( './../lib' );

var bool = isIntegerArray( [ -5, 0, 2, 5 ] );
console.log( bool );
// => true

bool = isIntegerArray( [ -4, -3, 1, 3 ] );
console.log( bool );
// => true

bool = isIntegerArray( [ -1, new Number( -6 ), 2 ] );
console.log( bool );
// => true

bool = isIntegerArray( [ -1, 'abc', 3 ] );
console.log( bool );
// => false

bool = isIntegerArray( [ -2.3, 0, 3 ] );
console.log( bool );
// => false

bool = isIntegerArray( [] );
console.log( bool );
// => false
