'use strict';

// MODULES //

var tape = require( 'tape' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var isInteger = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided an integer', function test( t ) {
	var i;
	for ( i = -50; i < 51; i++ ) {
		t.equal( isInteger( i ), true, 'returns true when provided '+i );
	}
	t.end();
});

tape( 'function supports `Number` objects', function test( t ) {
	t.equal( isInteger( new Number( 5 ) ), true, 'returns true' );
	t.equal( isInteger( new Number( 5.14 ) ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided an integer', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5.5,
		-1.14,
		1/0,
		NINF,
		NaN,
		true,
		null,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isInteger( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
