'use strict';

// MODULES //

var tape = require( 'tape' );
var isInteger = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided an integer', function test( t ) {
	t.ok( isInteger( 5 ) );
	t.ok( isInteger( 0 ) );
	t.ok( isInteger( -5 ) );
	t.ok( isInteger( new Number( 5 ) ) );
	t.end();
});

tape( 'function returns `false` if not provided an integer', function test( t ) {
	var values;
	var i;

	values = [
		5.5,
		true,
		1/0,
		Number.NEGATIVE_INFINITY,
		[],
		'5',
		function(){},
		null,
		{},
		NaN,
		undefined
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isInteger( values[i] ), false, 'returns false' );
	}
	t.end();
});
