'use strict';

/**
* Test whether a value corresponds to a leap year in the Gregorian calendar.
*
* @module @stdlib/utils/is-leap-year
*
* @example
* var isLeapYear = require( '@stdlib/utils/is-leap-year' );
*
* var bool = isLeapYear();
* // returns <boolean>
*
* bool = isLeapYear( new Date() );
* // returns <boolean>
*
* bool = isLeapYear( 1996 );
* // returns true
*
* bool = isLeapYear( 2001 );
* // returns false
*/

// MODULES //

var isLeapYear = require( './is_leap_year.js' );


// EXPORTS //

module.exports = isLeapYear;
