'use strict';

var isNaNArray = require( './../lib' );


var bool = isNaNArray( [NaN] );
console.log( bool );
// => true

bool = isNaNArray( [NaN,NaN,NaN] );
console.log( bool );
// => true

bool = isNaNArray( [new Number( NaN ),NaN,NaN] );
console.log( bool );
// => true

bool = isNaNArray( new Float64Array([NaN,NaN]) );
console.log( bool );
// => true

bool = isNaNArray( NaN );
console.log( bool );
// => false

bool = isNaNArray( ['a','b','c'] );
console.log( bool );
// => false

bool = isNaNArray( ['a',NaN] );
console.log( bool );
// => false
