'use strict';

var hasSymbolSupport = require( '@stdlib/utils/detect-symbol-support' )();
var isnan = require( './../lib' );

console.log( isnan( NaN ) );
// returns true

console.log( isnan( new Number( NaN ) ) );
// returns true

console.log( isnan( 5 ) );
// returns false

console.log( isnan( '5' ) );
// returns false

console.log( isnan( null ) );
// returns false

if ( hasSymbolSupport ) {
	console.log( isnan( Symbol( 'NaN' ) ) );
	// returns false
}
