'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isnan, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function provides a method to test for a generic number array having only `NaN` values', function test( t ) {
	var arr;

	arr = [ NaN, new Number( NaN ), NaN ];
	t.equal( isnan.isNaNArray( arr ), true, 'returns true' );

	arr = [ NaN, 3, NaN ];
	t.equal( isnan.isNaNArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for a primitive number array having only `NaN` values', function test( t ) {
	var arr;

	arr = [ NaN, NaN, NaN ];
	t.equal( isnan.isPrimitiveNaNArray( arr ), true, 'returns true' );

	arr = [ new Number( NaN ), NaN, NaN ];
	t.equal( isnan.isPrimitiveNaNArray( arr ), false, 'returns false' );

	t.end();
});
