'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( typeof isnan === 'function', 'main export is a function' );
	t.end();
});

tape( 'the fucntion should positively validate', function test( t ) {
		t.ok( isnan( NaN ), 'is true' );
		t.ok( isnan( new Number( NaN ) ), 'is true' );
		t.end();
	});

tape( 'the function should negatively validate', function test( t ) {
	var values = [
		true,
		[],
		'5',
		function(){},
		null,
		{},
		5,
		new Number( 5 ),
		undefined
	];

	for ( var i = 0; i < values.length; i++ ) {
		t.ok( !isnan( values[i] ), 'is false' );
	}
	t.end();
});
