'use strict';

// MODULES //

var tape = require( 'tape' );
var isNegativeInteger = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNegativeInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function provides a method to test for a generic number array having only negative integer values', function test( t ) {
	var arr;

	arr = [ -5.0, new Number( -5 ), -1.0 ];
	t.equal( isNegativeInteger.isNegativeIntegerArray( arr ), true, 'returns true' );

	arr = [ -5.0, '3', null ];
	t.equal( isNegativeInteger.isNegativeIntegerArray( arr ), false, 'returns false' );

	t.end();
});

tape( 'the function provides a method to test for a primitive number array having only negative integer values', function test( t ) {
	var arr;

	arr = [ -5.0, -1.0 ];
	t.equal( isNegativeInteger.isNegativePrimitiveIntegerArray( arr ), true, 'returns true' );

	arr = [ new Number( -5 ), -1.0, -1.0 ];
	t.equal( isNegativeInteger.isNegativePrimitiveIntegerArray( arr ), false, 'returns false' );

	t.end();
});
