'use strict';

// MODULES //

var tape = require( 'tape' );
var isNegativeInteger = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNegativeInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number having a negative integer value', function test( t ) {
	t.equal( typeof isNegativeInteger.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a negative integer value', function test( t ) {
	t.equal( typeof isNegativeInteger.isObject, 'function', 'export is a function' );
	t.end();
});
